    public function testShipTiddletBug() {
        /**
         * @SuppressWarnings("php:S1131")
         */
        $ship1 = static::$EXAMPLE_SHIP . '
BEGIN Objects    
    Size                 1  
    BEGIN "[i 0]"      
        Id                   9  
        Type                 Tiddlet  
        Damage               0  
    END
END';
        /**
         * @SuppressWarnings("php:S1131")
         */
        $ship2 = static::$EXAMPLE_SHIP . '
BEGIN Objects    
    Size                 1  
    BEGIN "[i 0]"      
        Id                   9  
        Type                 Tiddlet  
        Damage               0.3  
    END
END';

        $noBug = new ShipFile($ship1);
        $bug = new ShipFile($ship2);

        $this->assertTrue(TiddletBug::isShipBug());
        $this->assertTrue(TiddletBug::isSaveBug());
        $this->assertFalse(TiddletBug::hasBug($noBug));
        $this->assertTrue(TiddletBug::hasBug($bug));
        $this->assertTrue(TiddletBug::resolveBug($bug));
        $this->assertEquals($noBug, $bug);
    }
}
